const getImageDataURL = async () => {
  // https://github.com/googlefonts/noto-emoji/blob/main/png/512/emoji_u1f4c4.png
  const fallbackDataURL =
    "data:image/png;base64,";

  const url = document.querySelector('meta[property="og:image"]')?.content;

  if (!url) {
    return fallbackDataURL;
  }

  let response;

  try {
    response = await fetch(url);
  } catch (exception) {
    console.error(exception);

    return fallbackDataURL;
  }

  if (!response.ok) {
    return fallbackDataURL;
  }

  const blob = await response.blob();

  try {
    return await new Promise((resolve, reject) => {
      const fileReader = new FileReader();

      fileReader.onerror = () => {
        reject(fileReader.error);
      };

      fileReader.onload = () => {
        resolve(fileReader.result);
      };

      fileReader.readAsDataURL(blob);
    });
  } catch (exception) {
    console.error(exception);

    return fallbackDataURL;
  }
};

(async () => {
  const imageElement = new Image();

  imageElement.onload = async () => {
    const canvasElement = document.createElement("canvas");
    const canvasContext = canvasElement.getContext("2d");

    canvasElement.width = imageElement.naturalWidth;
    canvasElement.height = imageElement.naturalHeight + 1;

    canvasContext.drawImage(imageElement, 0, 0);

    [...Array(8).keys()].forEach((x) => {
      canvasContext.fillStyle = `rgba(${Math.floor(
        Math.random() * 256
      )}, ${Math.floor(Math.random() * 256)}, ${Math.floor(
        Math.random() * 256
      )},  ${1 / 255})`;

      canvasContext.fillRect(x, imageElement.naturalHeight, 1, 1);
    });

    const description = document.querySelector(
      'meta[name="description"]'
    )?.content;

    const title =
      document.querySelector('meta[property="og:title"]')?.content ??
      document.title;

    chrome.runtime.sendMessage({
      url: location.href,
      description,
      imageDataURL: canvasElement.toDataURL(),
      title,
    });
  };

  imageElement.src = await getImageDataURL();
})();
